PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" varchar NOT NULL);
CREATE TABLE "alternate_designations" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "designation" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "language_id" integer);
CREATE TABLE "languages" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "designation" text, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "location" text, "iso" varchar, "status" varchar, "orthography" varchar, "macro_classification" varchar, "glottocode" varchar, "latitude" decimal(5,2), "longitude" decimal(5,2));
CREATE TABLE "orthographies" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "languages_orthographies" ("language_id" integer NOT NULL, "orthography_id" integer NOT NULL);
CREATE TABLE "authors" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "first" varchar, "last" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "missionary_groups" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "publishers" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "text_categories" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "name" varchar);
CREATE TABLE "authors_texts" ("text_id" integer NOT NULL, "author_id" integer NOT NULL);
CREATE TABLE "languages_texts" ("language_id" integer NOT NULL, "text_id" integer NOT NULL);
CREATE TABLE "texts" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "missionary_group_id" integer, "publication_year" integer, "edition_note" text, "publication_place" varchar, "holding_library" varchar, "holding_details" varchar, "digital_copy_url" varchar, "hathi_url" varchar, "google_url" varchar, "text_categories_id" integer, "by_same_author_flag" boolean, "preface_flag" boolean, "foreward_flag" boolean, "table_of_prefixes_flag" boolean, "introduction_flag" boolean, "alphabet_flag" boolean, "orthography_flag" boolean, "notize_flag" boolean, "prounciation_flag" boolean, "orthography_and_pronunciation_flag" boolean, "observations_flag" boolean, "grammatical_sketch_flag" boolean, "grammar_summary_flag" boolean, "rules_of_language_flag" boolean, "phrase_collection_flag" boolean, "signs_and_abbreviations_flag" boolean, "abbreviations_flag" boolean, "sodality_flag" boolean, "bulletin_flag" boolean, "proper_names_flag" boolean, "appendix_flag" boolean, "addenda_flag" boolean, "contents_flag" boolean, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "title" varchar, "cover_image" varchar, "pages" integer);
CREATE TABLE "publishers_texts" ("text_id" integer NOT NULL, "publisher_id" integer NOT NULL);
CREATE TABLE "text_sections" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar, "from" integer, "to" integer, "ordinal" integer, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "parent_id" integer, "text_id" integer);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_languages_on_location" ON "languages" ("location");
CREATE INDEX "index_languages_on_iso" ON "languages" ("iso");
CREATE INDEX "index_languages_on_status" ON "languages" ("status");
CREATE INDEX "index_languages_on_orthography" ON "languages" ("orthography");
CREATE INDEX "index_languages_on_macro_classification" ON "languages" ("macro_classification");
CREATE INDEX "index_languages_on_glottocode" ON "languages" ("glottocode");
CREATE INDEX "index_lang_orth" ON "languages_orthographies" ("language_id", "orthography_id");
CREATE INDEX "index_orth_lang" ON "languages_orthographies" ("orthography_id", "language_id");
CREATE INDEX "index_authors_texts_on_text_id" ON "authors_texts" ("text_id");
CREATE INDEX "index_authors_texts_on_author_id" ON "authors_texts" ("author_id");
CREATE INDEX "index_languages_texts_on_language_id_and_text_id" ON "languages_texts" ("language_id", "text_id");
CREATE INDEX "index_languages_texts_on_text_id_and_language_id" ON "languages_texts" ("text_id", "language_id");
CREATE INDEX "index_publishers_texts_on_text_id_and_publisher_id" ON "publishers_texts" ("text_id", "publisher_id");
CREATE INDEX "index_publishers_texts_on_publisher_id_and_text_id" ON "publishers_texts" ("publisher_id", "text_id");
CREATE INDEX "index_text_sections_on_parent_id" ON "text_sections" ("parent_id");
CREATE INDEX "index_text_sections_on_text_id" ON "text_sections" ("text_id");
COMMIT;
